package ru.ql.tt.tests;import org.junit.AfterClass;import org.junit.BeforeClass;import org.openqa.selenium.WebDriver;import org.openqa.selenium.remote.BrowserType;import ru.ql.tt.appmanager.ApplicationManager;public class TestBase {    protected static final ApplicationManager app = new ApplicationManager(BrowserType.CHROME);    public WebDriver wd;    @BeforeClass    public static void setUp() throws Exception {        app.checkBrowser();        app.init();    }    @AfterClass    public static void tearDown(){        app.stop();    }}