package ru.ql.tt.tests;import org.openqa.selenium.remote.BrowserType;import org.testng.annotations.AfterClass;import org.testng.annotations.BeforeClass;import ru.ql.tt.appmanager.ApplicationManager;import ru.ql.tt.appmanager.UtilityMethods;public class TestBase {    public static ApplicationManager app = new ApplicationManager(BrowserType.CHROME);    UtilityMethods utilityMethods = new UtilityMethods();    @BeforeClass    public void setUp() {        app.createBrowser();        utilityMethods.init();        utilityMethods.goToProfile();    }    @AfterClass    public void tearDown(){        utilityMethods.stopBrowser();    }}