package ru.ql.tt.tests;import org.openqa.selenium.remote.BrowserType;import org.testng.annotations.AfterClass;import org.testng.annotations.BeforeClass;import ru.ql.tt.appmanager.ApplicationManager;import ru.ql.tt.appmanager.UtilityMethods;public class TestBase {    public static final ApplicationManager app = new ApplicationManager(BrowserType.CHROME);    @BeforeClass    public static void setUp() {        app.createBrowser();        UtilityMethods.init();        UtilityMethods.goToProfile();    }    @AfterClass    public static void tearDown(){        UtilityMethods.stop();    }}