package ru.ql.tt.tests;import org.openqa.selenium.remote.BrowserType;import org.testng.annotations.AfterClass;import org.testng.annotations.BeforeClass;import ru.ql.tt.appmanager.ApplicationManager;public class TestBase {    protected static final ApplicationManager app = new ApplicationManager(BrowserType.CHROME);    @BeforeClass    public static void setUp() throws Exception {        app.checkBrowser();        app.init();        app.goToProfile();    }    @AfterClass    public static void tearDown(){        app.stop();    }}